<script>
import { store } from "../store";
import axios from "axios";
import tt from "@tomtom-international/web-sdk-maps";
import { TouchPitchHandler } from "mapbox-gl";

export default {
  name: "Details",
  data() {
    return {
      store,
      apartment: {},
      basicServices: [
        {
          icon: "fa-solid fa-house fs-3",
          title: "Entire home",
          subTitle: "You'll have the apartment to yourself",
        },
        {
          icon: "fa-solid fa-spray-can-sparkles",
          title: "Enhanced Clean",
          subTitle:
            "This Host committed to Airnb's 5-step enhanced cleaning process.",
        },
        {
          icon: "fa-solid fa-door-closed",
          title: "Self check-in",
          subTitle: "Check yourself in with the keypad",
        },
        {
          icon: "fa-regular fa-calendar-minus",
          title: "Free cancellation before Feb 14",
          subTitle: "",
        },
      ],
      fullnameForm: "",
      emailForm: "",
      messageForm: "",
      errorFullname: false,
      errorEmail: false,
      errorMessage: false,
    };
  },
  methods: {
    getApartment() {
      const slug = this.$route.params.slug;
      axios.get(`${this.store.apiUrl}/api/apartments/${slug}`).then((resp) => {
        this.apartment = resp.data.apartment;
        this.createMap(
          this.apartment.address.latitude,
          this.apartment.address.longitude,
          this.store.keyTomTom
        );
      });
    },

    createMap(lat, long, key) {
      var map = tt.map({
        key,
        container: "map",
        center: [long, lat],
        zoom: 15,
      });

      var marker = new tt.Marker().setLngLat([long, lat]).addTo(map);
    },

    sendMessage() {
      this.errorFullname = false;
      this.errorEmail = false;
      this.errorMessage = false;
      if (this.fullnameForm && this.emailForm && this.messageForm) {
        const params = {
          apartment_id: this.apartment.id,
          fullname: this.fullnameForm,
          email: this.emailForm,
          message: this.messageForm,
        };

        axios
          .post(`${this.store.apiUrl}/api/add-message`, params)
          .then((resp) => {
            console.log(resp);
            this.$router.push({ name: "sent-message" });
          })
          .catch((err) => {
            console.log(err);
          });
      } else {
        if (!this.fullnameForm) {
          this.errorFullname = true;
        }

        if (!this.emailForm) {
          this.errorEmail = true;
        }

        if (!this.messageForm) {
          this.errorMessage = true;
        }
      }
    },
  },
  created() {
    this.getApartment();
  },
};
</script>

